mapper.select:
select tr.id, tr.name, cus.name as customerId, tdv1.value as stage, tdv2.value as type, u.name as owner, tdv3.value as source, con.fullname as contactsId from tbl_tran tr join tbl_customer cus on tr.customer_id = cus.id join tbl_dic_value tdv1 on tr.stage = tdv1.id left join tbl_dic_value tdv2 on tr.type = tdv2.id join tbl_user u on tr.owner = u.id left join tbl_dic_value tdv3 on tr.source = tdv3.id left join tbl_contacts con on tr.contacts_id = con.id
and u.name like '%' #{owner} '%'
and tr.name like '%' #{name} '%'
and cus.name like '%' #{customerId} '%'
and tdv1.id=#{stage}
and tdv2.id=#{type}
and tdv3.id=#{source}
and con.fullname like '%' #{contactsId} '%'
order by tr.create_time desc limit #{limitCount},#{pageSize}

select count(*) from tbl_tran tr join tbl_customer cus on tr.customer_id = cus.id join tbl_dic_value tdv1 on tr.stage = tdv1.id left join tbl_dic_value tdv2 on tr.type = tdv2.id join tbl_user u on tr.owner = u.id left join tbl_dic_value tdv3 on tr.source = tdv3.id left join tbl_contacts con on tr.contacts_id = con.id
and u.name like '%' #{owner} '%'
and tr.name like '%' #{name} '%'
and cus.name like '%' #{customerId} '%'
and tdv1.id=#{stage}
and tdv2.id=#{type}
and tdv3.id=#{source}
and con.fullname like '%' #{contactsId} '%'
